    private void btnCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCActionPerformed
        formatear();
        calculo = new Calculos(matrizX2);
        int det = calculo.CalculoDeterX2(matrizX2);
        lblD.setText(String.valueOf(det));
       
        int x = calculo.CalculoVarX(det, matrizX2);
        int detX = matrizX2[0][2]*matrizX2[1][1] - (matrizX2[1][2]*matrizX2[0][1]);

        int y = calculo.CalculoVarY(det, matrizX2);
        lblY.setText(String.valueOf(y));
        lblX.setText(String.valueOf(x));
        System.out.println(x + " " + y +" "+detX);
        for (int l[]:matrizX2) {
            System.out.println(); //salto de linea cuando termina la primera fila
            for (int l1:l) {
                 System.out.print(l1 + " ");

           }
        }
        

    }//GEN-LAST:event_btnCActionPerformed

