    private void formatear() {
        matrizX2[0][0] = Integer.parseInt(txtX1.getText());
        matrizX2[0][1] = Integer.parseInt(txtY1.getText());
        matrizX2[0][2] = Integer.parseInt(txtR1.getText());        
        matrizX2[1][0] = Integer.parseInt(txtX2.getText());
        matrizX2[1][1] = Integer.parseInt(txtY2.getText());
        matrizX2[1][2] = Integer.parseInt(txtR2.getText());
        
    }
